INSERT INTO `rating` (`id`, `review`, `author`, `review_source`, `rating`, `title`, `product_name`, `reviewed_date`) VALUES
	(856, 'The biggest bug bear with this is the fact that using the tunein app to find a radio station is harder than it should be. There should be a way of storing your favorite stations so they\'re easier to access rather than having to search every time.', 'jack wilkinson', 'GooglePlayStore', 4, '', 'Amazon Alexa', '2017-06-06 00:00:00'),
	(857, 'Please update, the app doesn\'t work on the new Samsung Galaxy S8 plus', 'Nick Cudworth', 'GooglePlayStore', 1, '', 'Amazon Alexa', '2017-06-06 00:00:00'),
