INSERT INTO `rating` (`id`, `review`, `author`, `review_source`, `rating`, `title`, `product_name`, `reviewed_date`) VALUES
	(1791, 'Will man Alexa in englischer Sprache verwenden, benötigt man einen amazon account der UK/US basierend ist. Sonst stehen keinerlei skills zur Verfügung.   "Die ausgewählte Spracheinstellung stimmt nicht mit den Einstellungen Ihres Amazon-Kontos überein.  Daher werden Sie nicht auf Skills zugreifen können."  Wenn man den Account auf z.B. UK umstellt, hat man zwar die skills, aber es fehlt z.B. das Prime musik abbo welches dann immer noch nur für den deutschen Account gültig ist.  Deshalb, für internationale Leute in Deutschland unbrauchbar.', '', 'GooglePlayStore', 2, '', 'Amazon Alexa', '2017-07-22 00:00:00'),
	(1792, 'Had my echo dot and app for about 2 weeks. Not been able to use the app as the app takes over 10 minutes to load. Amazon need to put some investment into this and sort it out.', 'Ryan Gains', 'GooglePlayStore', 1, '', 'Amazon Alexa', '2017-07-22 00:00:00'),
