INSERT INTO `rating` (`id`, `review`, `author`, `review_source`, `rating`, `title`, `product_name`, `reviewed_date`) VALUES
	(3174, 'It amazed me that Amazon scraped by for years with a barely working, sorry excuse for an app, only now deciding it was time for a change. I was actually happy to see this reskin, hoping they\'d finally addressed the horrible performance issues. I was optimistic, only to find out it\'s just that - a reskin. The app is still as slow and unresponsive than it ever was. Plus, Amazon shoehorned this stupid "calling" feature that I do not want my Echo to have, and I have no way to disable it or opt out. This app is garbage, and the Echo is slowly turning into a paperweight. Won\'t be long until I give up on it altogether.', 'Brandon Serpas', 'GooglePlayStore', 1, '', 'Amazon Alexa', '2017-11-07 00:00:00'),
	(3175, 'The Bluetooth speaker feature is not working at all with my one plus five. Request for an speedy update.', 'partha baruah', 'GooglePlayStore', 3, '', 'Amazon Alexa', '2017-11-07 00:00:00'),
	(3176, 'Its good for nothing.i am trying to setup my new echo device since from 2 hours but its not recognising the wi-fi of echo device.getting really pissed.', 'Ankur Verma', 'GooglePlayStore', 1, '', 'Amazon Alexa', '2017-11-07 00:00:00'),
	(3177, 'App is slow.', 'Rushi Chincholkar', 'GooglePlayStore', 5, '', 'Amazon Alexa', '2017-11-08 00:00:00'),
