INSERT INTO `rating` (`id`, `review`, `author`, `review_source`, `rating`, `title`, `product_name`, `reviewed_date`) VALUES
	(4921, 'This is  nice app but really needs to integrate with other Amazon services better. I having to look up a song in Amazon Music to figure out what to tell Alexa to play because she plays something different by default. Otherwise, Alexa\'s functionality is great despite few bugs I have encountered as an Echo power user.', 'Ramy Habash', 'GooglePlayStore', 4, '', 'Amazon Alexa', '2018-01-15 00:00:00'),
	(4922, 'It\'s an amazing app', '', 'GooglePlayStore', 5, '', 'Amazon Alexa', '2018-01-15 00:00:00'),
	(4923, 'Works about 50% of the time.  The functionality is there, but the communication link between app and device is what constantly fails, which pretty much renders the app useless.  Also, the volume dial is a pain to use.', 'Juston Ling', 'GooglePlayStore', 2, '', 'Amazon Alexa', '2018-01-15 00:00:00'),
	(4924, 'New & still learning. Confusiming skill home set up automatically', 'Ann Schmidt', 'GooglePlayStore', 5, '', 'Amazon Alexa', '2018-01-15 00:00:00'),
